import React, { Component } from 'react';
// import logo from './logo.svg';
import { Container, Row, Col, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem,InputGroup, InputGroupAddon, Input } from 'reactstrap'
import './App.css';

class App extends Component {

    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            dropdownOpen: false,
            selectValue: 'Select a State',
            tableData: []
        };
    }

    toggle = () => {
        this.setState({
            dropdownOpen: !this.state.dropdownOpen
        });
    }

    handleChange = (e) => {
        this.setState({selectValue:e.target.value});
        console.log(this.state.selectValue,'select value')
    }

    renderTable = () => {
        let bankAPI = `https://vast-shore-74260.herokuapp.com/banks?city=${this.state.selectValue}`
        axios.get(bankAPI).then(res=> {
            console.log(res.data);
            this.setState({tableData: res.data})
        })
    }

  render() {
    return (
      <Container>
          <Row>
            <h1>BANK LIST</h1>
          </Row>
          <Row>
              <Col>
                  <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                      <DropdownToggle caret size="sm">
                          {this.state.selectValue}
                      </DropdownToggle>
                      <DropdownMenu onClick={this.handleChange}>
                          <DropdownItem value='MUMBAI'>MUMBAI</DropdownItem>
                          <DropdownItem value='KERALA'>KERALA</DropdownItem>
                      </DropdownMenu>
                  </ButtonDropdown>
              </Col>
              <Col>
                  <InputGroup>
                      <InputGroupAddon addonType="prepend">@</InputGroupAddon>
                      <Input placeholder="username" />
                  </InputGroup>
              </Col>
          </Row>
          <Row>
              {this.renderTable}
          </Row>
      </Container>
    );
  }
}

export default App;
