import React, { Component } from 'react';
import axios from 'axios';
import { Container, Row, Col, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem,InputGroup, InputGroupAddon, Input } from 'reactstrap'
import './App.css';

class App extends Component {

    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            dropdownOpen: false,
            selectValue: 'Select a State',
            tableData: [],
            value: ''
        };
    }

    toggle = () => {
        this.setState({
            dropdownOpen: !this.state.dropdownOpen
        });
    }

    api = () => {
        let bankAPI = `https://vast-shore-74260.herokuapp.com/banks?city=${this.state.value}`
        console.log(bankAPI, 'API')
        axios.get(bankAPI).then(res=> {
            console.log(res.data);
            this.setState({tableData: res.data})
        });
    }
    handleChange = (e) => {
        this.setState({selectValue:e.target.value});
        console.log(this.state.selectValue,'select value')
    }

    showTable = () => {
        {this.api()};
        if(this.state.tableData.length !== 0) {
            return(
                <Container>
                    <hr/>
                    {/*{this.state.tableData.map((obj,i) => (*/}
                        {/*<Table key={i} tableData={obj}/>*/}
                    {/*))}*/}
                    <h1>HAI this is table</h1>
                    </Container>
            );
        }
    }

  render() {
    return (
      <Container>
          <Row>
            <h1>BANK LIST</h1>
          </Row>
          <Row>
              <Col md="3">
                  <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                      <DropdownToggle caret size="sm">
                          {this.state.selectValue}
                      </DropdownToggle>
                      <DropdownMenu onClick={this.handleChange}>
                          <DropdownItem value='MUMBAI'>MUMBAI</DropdownItem>
                          <DropdownItem value='KERALA'>KERALA</DropdownItem>
                      </DropdownMenu>
                  </ButtonDropdown>
              </Col>
              <Col md="9">
                  <InputGroup>
                      <InputGroupAddon addonType="prepend">Search</InputGroupAddon>
                      <Input placeholder="Enter the name" />
                  </InputGroup>
              </Col>
          </Row>
          <Row>
              {this.showTable()}
          </Row>
      </Container>
    );
  }
}

export default App;
