import React, { Component } from 'react';
// import logo from './logo.svg';
import { Container, Row, Col, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap'
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            dropdownOpen: false,
            selectValue: 'Select a State'
        };
    }

    toggle = () => {
        this.setState({
            dropdownOpen: !this.state.dropdownOpen
        });
    }

    handleChange = (e) => {
        this.setState({selectValue:e.target.value});
    }

  render() {
    return (
      <Container>
          <Row>
            <h1>BANK LIST</h1>
          </Row>
          <Row>
              <Col>
                  <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                      <DropdownToggle caret size="sm">
                          Select a State
                      </DropdownToggle>
                      <DropdownMenu onChange={this.handleChange}>
                          <DropdownItem>MUMBAI</DropdownItem>
                          <DropdownItem>KERALA</DropdownItem>
                      </DropdownMenu>
                  </ButtonDropdown>
              </Col>
          </Row>

      </Container>
    );
  }
}

export default App;
